  `email` varchar(50) DEFAULT NULL,
  `password` varchar(10) DEFAULT NULL,
  `status` int(10) DEFAULT NULL,
  KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4;

